import React from "react";


export default function Serie(props){

const { serie } = props;
const genres = serie.genres.map( genres =>
     <span key={genres}>{genres}</span>); 

return (
    <div className="color">
        <section >
            <div className="banniere">
            <img src={serie.images.banner} alt="banniere"></img>
            <h1> {serie.title}</h1>
            </div>
            <div>
                <p>{serie.description}</p>
            </div>
            <div>
                <p>{genres}</p>

            </div>
            <button>
            <i className="glyphicon glyphicon-heart-empty"></i>

            </button>

        </section>
    </div>
);
}
